import React, { useEffect, useState } from 'react'

const App = () => {

 
  const [ingredientList, setIngredientList] = useState([])
  const API_KEY = "b46c142bd0ae81837c2625c4be4314ad"
  const APP_ID = 'd0f9f036'

useEffect(() => {
 let url = `https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${API_KEY}`
 fetch(url).then(response => response.json())
 .then((res) =>
  {console.log(res.hits)
  setIngredientList(res.hits) })
 .catch((err) => {
  console.log(err);
 })


},[])



  return (
    <div className='container'>
    <div className='header'>
        <input type='text' placeholder='Serach'/>
        <button>Serach</button>

      <div className='box'>
        {ingredientList?.map(item => {
          return(
             <div className='card'>
             <span>{item.recipe.label}</span>
             <img src={item.recipe.image} alt=''/>
             <div className='steps'>
             {item.recipe.ingredientLines.map((step) => {
              return(
                <p>{step}</p>
              )
             
             })}
             </div>
             </div>
          );
        })}

      </div>
    </div>

    </div>
  )
}

export default App
